  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(268 50% 15%);
  --sidebar-accent-foreground: hsl(262 83% 58%);
  --sidebar-border: hsl(268 25% 25%);
  --sidebar-ring: hsl(262 83% 58%);
  --font-sans: Open Sans, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 1.3rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 1px 2px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 1px 2px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 2px 4px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 4px 6px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 8px 10px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00);
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    background: hsl(260 20% 8%);
  }

  body {
    @apply font-sans antialiased text-foreground;
    background: hsl(260 20% 8%);
    min-height: 100vh;
  }

  #root {
    background: hsl(260 20% 8%);
    min-height: 100vh;
  }
}

/* Dragon theme styles */
.bg-dragon-gradient {
  background: linear-gradient(135deg, hsl(262 80% 8%) 0%, hsl(262 60% 15%) 30%, hsl(268 50% 25%) 70%, hsl(262 80% 8%) 100%);
}

.corrupted-soul {
  background: linear-gradient(90deg, var(--soul-corrupt) 0%, hsl(0 50% 30%) 100%);
  animation: pulse-corrupt 1.5s infinite;
}

.pure-soul {
  background: linear-gradient(90deg, var(--soul-pure) 0%, hsl(210 20% 95%) 100%);
}

.narrative-text {
  line-height: 1.8;
  text-align: justify;
}

.choice-button:hover {
  transform: translateY(-2px);
  transition: all 0.3s ease;
}

.tribal-border {
  border-image: linear-gradient(45deg, var(--dragon-gold), var(--dragon-purple)) 1;
}

@keyframes pulse-corrupt {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.7; }
}

/* Custom range slider styling */
.slider::-webkit-slider-thumb {
  appearance: none;
  height: 16px;
  width: 16px;
  border-radius: 50%;
  background: hsl(280.4, 72.8%, 55.7%);
  cursor: pointer;
}

.slider::-moz-range-thumb {
  height: 16px;
  width: 16px;
  border-radius: 50%;
  background: hsl(280.4, 72.8%, 55.7%);
  cursor: pointer;
  border: none;
}

@keyframes glow {
  from { box-shadow: 0 0 5px var(--dragon-gold), 0 0 10px var(--dragon-gold), 0 0 15px var(--dragon-gold); }
  to { box-shadow: 0 0 10px var(--dragon-gold), 0 0 20px var(--dragon-gold), 0 0 30px var(--dragon-gold); }
}

.animate-glow {
  animation: glow 2s ease-in-out infinite alternate;
}

