import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Dice6, Sparkles, Zap, Shuffle } from "lucide-react";
import { generateCharacter } from "@/lib/character-generator";
import { Character } from "@shared/schema";

const TRIBES = [
  "MudWing", "SandWing", "SkyWing", "SeaWing", "IceWing", 
  "RainWing", "NightWing", "SilkWing", "HiveWing", "LeafWing", "Hybrid"
];

const AVAILABLE_TRAITS = [
  "Brave", "Cunning", "Wise", "Curious", "Fierce", "Gentle", "Loyal", "Mysterious",
  "Ambitious", "Calm", "Reckless", "Patient", "Proud", "Humble", "Vengeful", "Forgiving",
  "Studious", "Impulsive", "Cautious", "Bold", "Sarcastic", "Optimistic", "Pessimistic", "Protective"
];

const TRIBAL_POWERS = {
  MudWing: ["Fire resistance", "Mud camouflage", "Enhanced strength when warm"],
  SandWing: ["Venomous tail barb", "Desert survival", "Heat resistance"],
  SkyWing: ["Superior flight speed", "Fire breathing", "High altitude adaptation"],
  SeaWing: ["Underwater breathing", "Bioluminescent scales", "Deep sea pressure resistance"],
  IceWing: ["Frost breath", "Cold immunity", "Serrated claws"],
  RainWing: ["Color-changing scales", "Acidic venom", "Prehensile tail"],
  NightWing: ["Future sight", "Mind reading", "Fire breathing"],
  SilkWing: ["Silk production", "Metamorphosis", "Enhanced agility"],
  HiveWing: ["Paralytic stinger", "Hive mind connection", "Toxic breath"],
  LeafWing: ["Plant manipulation", "Leaf speak", "Photosynthesis healing"]
};

interface CharacterCreatorProps {
  onCreateCharacter: (character: Character) => void;
  onCancel: () => void;
}

export default function CharacterCreator({ onCreateCharacter, onCancel }: CharacterCreatorProps) {
  const [customName, setCustomName] = useState("");
  const [selectedTribe, setSelectedTribe] = useState<string>("");
  const [isAnimus, setIsAnimus] = useState(false);
  const [selectedPowers, setSelectedPowers] = useState<string[]>([]);
  const [selectedTraits, setSelectedTraits] = useState<string[]>([]);
  const [selectedHybridTribes, setSelectedHybridTribes] = useState<string[]>([]);
  const [attributes, setAttributes] = useState({
    strength: 50,
    intelligence: 50,
    charisma: 50,
    wisdom: 50
  });

  const handleRandomize = () => {
    const randomChar = generateCharacter();
    setCustomName(randomChar.name);
    setSelectedTribe(randomChar.hybridTribes ? "Hybrid" : randomChar.tribe);
    setIsAnimus(randomChar.isAnimus);
    setSelectedPowers(randomChar.tribalPowers);
    setSelectedTraits(randomChar.traits);
    setSelectedHybridTribes(randomChar.hybridTribes || []);
    setAttributes({
      strength: randomChar.strength,
      intelligence: randomChar.intelligence,
      charisma: randomChar.charisma,
      wisdom: randomChar.wisdom
    });
  };

  const handlePowerToggle = (power: string) => {
    setSelectedPowers(prev => 
      prev.includes(power) 
        ? prev.filter(p => p !== power)
        : [...prev, power]
    );
  };

  const handleTraitToggle = (trait: string) => {
    setSelectedTraits(prev => 
      prev.includes(trait) 
        ? prev.filter(t => t !== trait)
        : [...prev, trait]
    );
  };

  const handleHybridTribeToggle = (tribe: string) => {
    setSelectedHybridTribes(prev => 
      prev.includes(tribe) 
        ? prev.filter(t => t !== tribe)
        : prev.length < 3 ? [...prev, tribe] : prev
    );
  };

  const randomizeTraits = () => {
    const shuffled = [...AVAILABLE_TRAITS].sort(() => Math.random() - 0.5);
    setSelectedTraits(shuffled.slice(0, 2 + Math.floor(Math.random() * 3)));
  };

  const handleCreateCustom = () => {
    const baseChar = generateCharacter();
    const customCharacter: Character = {
      ...baseChar,
      name: customName || baseChar.name,
      tribe: selectedTribe === "Hybrid" ? (selectedHybridTribes[0] || "MudWing") : selectedTribe || baseChar.tribe,
      hybridTribes: selectedTribe === "Hybrid" ? selectedHybridTribes : undefined,
      isAnimus,
      tribalPowers: selectedPowers,
      traits: selectedTraits,
      strength: attributes.strength,
      intelligence: attributes.intelligence,
      charisma: attributes.charisma,
      wisdom: attributes.wisdom,
      soulPercentage: 100,
      sanityPercentage: 100
    };
    onCreateCharacter(customCharacter);
  };

  const availablePowers = (() => {
    if (selectedTribe === "Hybrid" && selectedHybridTribes.length > 0) {
      // Combine powers from all selected hybrid tribes
      const combinedPowers: string[] = [];
      selectedHybridTribes.forEach(tribe => {
        const tribePowers = TRIBAL_POWERS[tribe as keyof typeof TRIBAL_POWERS] || [];
        combinedPowers.push(...tribePowers);
      });
      return Array.from(new Set(combinedPowers)); // Remove duplicates
    }
    return selectedTribe ? TRIBAL_POWERS[selectedTribe as keyof typeof TRIBAL_POWERS] || [] : [];
  })();

  return (
    <div className="min-h-screen bg-dragon-gradient text-slate-100 p-4">
      <div className="container mx-auto max-w-4xl">
        <Card className="bg-black/40 backdrop-blur-sm border-purple-500/30">
          <CardHeader>
            <CardTitle className="font-fantasy text-3xl text-purple-300 text-center">
              <span className="mr-3">üêâ</span>
              Create Your Dragon
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Quick Actions */}
            <div className="flex gap-4 justify-center">
              <Button 
                onClick={handleRandomize}
                className="bg-purple-600 hover:bg-purple-700"
              >
                <Dice6 className="w-4 h-4 mr-2" />
                Randomize Everything
              </Button>
              <Button 
                onClick={() => onCreateCharacter(generateCharacter())}
                variant="outline"
              >
                <Sparkles className="w-4 h-4 mr-2" />
                Quick Start (Random)
              </Button>
            </div>

            <div className="grid md:grid-cols-2 gap-6">
              {/* Basic Info */}
              <div className="space-y-4">
                <h3 className="font-fantasy text-xl text-purple-300">Basic Information</h3>

                <div>
                  <Label htmlFor="name">Dragon Name</Label>
                  <Input
                    id="name"
                    value={customName}
                    onChange={(e) => setCustomName(e.target.value)}
                    placeholder="Enter custom name or leave blank for random"
                    className="bg-black/50 border-purple-500/30"
                  />
                </div>

                <div>
                  <Label htmlFor="tribe">Tribe</Label>
                  <Select value={selectedTribe} onValueChange={setSelectedTribe}>
                    <SelectTrigger className="bg-black/50 border-purple-500/30">
                      <SelectValue placeholder="Choose your tribe" />
                    </SelectTrigger>
                    <SelectContent>
                      {TRIBES.map(tribe => (
                        <SelectItem key={tribe} value={tribe}>{tribe}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox 
                    id="animus" 
                    checked={isAnimus}
                    onCheckedChange={(checked) => setIsAnimus(checked === true)}
                  />
                  <Label htmlFor="animus" className="flex items-center">
                    <Zap className="w-4 h-4 mr-2 text-red-400" />
                    Animus Dragon (Soul magic abilities)
                  </Label>
                </div>

                {/* Hybrid Tribe Selection */}
                {selectedTribe === "Hybrid" && (
                  <div className="space-y-3">
                    <Label>Choose Hybrid Tribes (2-3 tribes)</Label>
                    <div className="grid grid-cols-2 gap-2">
                      {TRIBES.filter(t => t !== "Hybrid").map(tribe => (
                        <div 
                          key={tribe}
                          className={`p-2 rounded-lg border text-sm cursor-pointer transition-colors ${
                            selectedHybridTribes.includes(tribe)
                              ? 'bg-purple-600/50 border-purple-400'
                              : 'bg-black/30 border-purple-500/30 hover:bg-purple-500/20'
                          }`}
                          onClick={() => handleHybridTribeToggle(tribe)}
                        >
                          {tribe}
                        </div>
                      ))}
                    </div>
                    <p className="text-xs text-slate-400">
                      Select 2-3 tribes for your hybrid heritage. Order matters - first tribe is primary.
                    </p>
                  </div>
                )}
              </div>

              {/* Attributes */}
              <div className="space-y-4">
                <h3 className="font-fantasy text-xl text-purple-300">Attributes</h3>

                {Object.entries(attributes).map(([attr, value]) => (
                  <div key={attr}>
                    <Label className="capitalize">{attr}: {value}</Label>
                    <input
                      type="range"
                      min="10"
                      max="90"
                      value={value}
                      onChange={(e) => setAttributes(prev => ({
                        ...prev,
                        [attr]: parseInt(e.target.value)
                      }))}
                      className="w-full h-2 bg-black/50 rounded-lg appearance-none cursor-pointer slider"
                    />
                  </div>
                ))}
              </div>
            </div>

            {/* Tribal Powers */}
            {selectedTribe && availablePowers.length > 0 && (
              <div className="space-y-4">
                <h3 className="font-fantasy text-xl text-purple-300">
                  {selectedTribe === "Hybrid" ? "Hybrid" : selectedTribe} Tribal Powers
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  {availablePowers.map(power => (
                    <div 
                      key={power}
                      className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                        selectedPowers.includes(power)
                          ? 'bg-purple-600/50 border-purple-400'
                          : 'bg-black/50 border-purple-500/30 hover:bg-purple-500/20'
                      }`}
                      onClick={() => handlePowerToggle(power)}
                    >
                      <div className="flex items-center justify-between">
                        <span className="text-sm">{power}</span>
                        {selectedPowers.includes(power) && (
                          <Badge variant="secondary" className="ml-2">Selected</Badge>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
                <p className="text-xs text-slate-400">
                  Select the powers your dragon has mastered. You can choose multiple abilities.
                </p>
              </div>
            )}

            {/* Character Traits */}
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="font-fantasy text-xl text-purple-300">Character Traits</h3>
                <Button 
                  onClick={randomizeTraits}
                  size="sm"
                  variant="outline"
                  className="text-xs"
                >
                  <Dice6 className="w-3 h-3 mr-1" />
                  Randomize
                </Button>
              </div>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                {AVAILABLE_TRAITS.map(trait => (
                  <div 
                    key={trait}
                    className={`p-2 rounded-lg border text-sm cursor-pointer transition-colors ${
                      selectedTraits.includes(trait)
                        ? 'bg-purple-600/50 border-purple-400'
                        : 'bg-black/30 border-purple-500/30 hover:bg-purple-500/20'
                    }`}
                    onClick={() => handleTraitToggle(trait)}
                  >
                    {trait}
                  </div>
                ))}
              </div>
              <p className="text-xs text-slate-400">
                Select 2-5 traits that define your dragon's personality and behavior.
              </p>
            </div>

            {/* Action Buttons */}
            <div className="flex gap-4 justify-center pt-6">
              <Button 
                onClick={handleCreateCustom}
                size="lg"
                className="bg-purple-600 hover:bg-purple-700"
              >
                Create Character
              </Button>
              <Button 
                onClick={onCancel}
                variant="outline"
                size="lg"
              >
                Cancel
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/character-panel.ts.content:
import { Character, InventoryItem } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Button } from "@/components/ui/button";
import { GameEngine } from "@/lib/game-engine";
import { getHybridDisplayName, getHybridPowerDescription } from "@/lib/hybrid-generator";
import SanityBar from "./sanity-bar";
import { Zap, Eye, Sparkles, Package, Heart, Users, Baby } from "lucide-react";
import { useState } from "react";

interface CharacterPanelProps {
  character: Character;
  inventory?: InventoryItem[];
  onShowTribalPowers?: () => void;
  onUseInventoryItem?: (item: InventoryItem) => void;
}

export default function CharacterPanel({ character, inventory = [], onShowTribalPowers, onUseInventoryItem }: CharacterPanelProps) {
  const [activeTab, setActiveTab] = useState<"info" | "family" | "social" | "inventory">("info");
  const corruptionLevel = GameEngine.getCorruptionLevel(character.soulPercentage);

  const getSoulBarColor = () => {
    if (character.soulPercentage >= 80) return "bg-white";
    if (character.soulPercentage >= 50) return "bg-yellow-400";
    if (character.soulPercentage >= 20) return "bg-orange-500";
    return "bg-red-600 animate-pulse";
  };

  return (
    <div className="lg:col-span-1">
      <Card className="bg-black/40 backdrop-blur-sm border-purple-500/30 h-full overflow-y-auto">
        <CardContent className="p-6">
          {/* Character Avatar & Basic Info */}
          <div className="text-center mb-6">
            <img
              src={character.avatar}
              alt="Dragon character portrait"
              className="w-32 h-32 rounded-full mx-auto mb-4 border-4 border-purple-400 object-cover"
            />
            <h2 className="font-fantasy text-2xl font-bold text-purple-300">
              {character.name}
            </h2>
            <p className="text-purple-400 font-medium">
              {character.hybridTribes ? getHybridDisplayName(character) : `${character.tribe} Dragonet`}
            </p>
            {character.hybridTribes && (
              <div className="mt-2">
                <Badge variant="outline" className="border-rainbow bg-gradient-to-r from-purple-500/20 to-blue-500/20">
                  <Sparkles className="w-3 h-3 mr-1" />
                  Hybrid Dragon
                </Badge>
              </div>
            )}
            <p className="text-sm text-purple-200">
              {character.age} years old
            </p>
          </div>

          {/* Soul Status */}
          <div className="mb-6">
            <div className="flex items-center justify-between mb-2">
              <span className="font-medium text-slate-200">Soul Integrity</span>
              <span className="text-sm font-bold">{character.soulPercentage}%</span>
            </div>
            <Progress
              value={character.soulPercentage}
              className="h-3"
            />
            <div className="flex items-center justify-between mt-1">
              <Badge variant={character.soulCorruptionStage === 'Normal' ? 'default' : character.soulCorruptionStage === 'Frayed' ? 'secondary' : character.soulCorruptionStage === 'Twisted' ? 'destructive' : 'destructive'} className="text-xs">
                {character.soulCorruptionStage}
              </Badge>
              <span className="text-xs text-slate-400">
                {character.soulCorruptionStage === 'Normal' ? 'Pure soul' : character.soulCorruptionStage === 'Frayed' ? 'Minor corruption' : character.soulCorruptionStage === 'Twisted' ? 'Significant corruption' : 'Soul nearly lost'}
              </span>
            </div>
          </div>

          {/* Sanity Status */}
          <div className="mb-6">
            <SanityBar sanityPercentage={character.sanityPercentage} />
          </div>

          {/* Animus & Powers Status */}
          <div className="mb-6">
            <div className="space-y-3">
              {character.isAnimus && (
                <Badge variant="destructive" className="w-full justify-center py-2">
                  <Zap className="w-4 h-4 mr-2" />
                  Animus Dragon
                </Badge>
              )}

              {(character.tribalPowers.length > 0 || character.specialPowers.length > 0) && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={onShowTribalPowers}
                  className="w-full"
                >
                  <Sparkles className="w-4 h-4 mr-2" />
                  View Powers ({character.tribalPowers.length + character.specialPowers.length})
                </Button>
              )}
            </div>
          </div>

          {/* Navigation Tabs */}
          <div className="flex space-x-1 mb-4">
            <Button
              variant={activeTab === "info" ? "default" : "ghost"}
              size="sm"
              onClick={() => setActiveTab("info")}
              className="flex-1"
            >
              <Eye className="w-3 h-3 mr-1" />
              Info
            </Button>
            <Button
              variant={activeTab === "family" ? "default" : "ghost"}
              size="sm"
              onClick={() => setActiveTab("family")}
              className="flex-1"
            >
              <Baby className="w-3 h-3 mr-1" />
              Family
            </Button>
            <Button
              variant={activeTab === "social" ? "default" : "ghost"}
              size="sm"
              onClick={() => setActiveTab("social")}
              className="flex-1"
            >
              <Users className="w-3 h-3 mr-1" />
              Social
            </Button>
            <Button
              variant={activeTab === "inventory" ? "default" : "ghost"}
              size="sm"
              onClick={() => setActiveTab("inventory")}
              className="flex-1"
            >
              <Package className="w-3 h-3 mr-1" />
              Items
            </Button>
          </div>

          {/* Tab Content */}
          {activeTab === "info" && (
            <div className="space-y-4">
              {/* Character Stats */}
              <div>
                <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                  Attributes
                </h3>
                <div className="grid grid-cols-2 gap-3">
                  <div className="bg-black/50 rounded-lg p-3">
                    <div className="text-xs text-slate-400">Strength</div>
                    <div className="font-semibold">{character.strength}</div>
                  </div>
                  <div className="bg-black/50 rounded-lg p-3">
                    <div className="text-xs text-slate-400">Intelligence</div>
                    <div className="font-semibold">{character.intelligence}</div>
                  </div>
                  <div className="bg-black/50 rounded-lg p-3">
                    <div className="text-xs text-slate-400">Charisma</div>
                    <div className="font-semibold">{character.charisma}</div>
                  </div>
                  <div className="bg-black/50 rounded-lg p-3">
                    <div className="text-xs text-slate-400">Wisdom</div>
                    <div className="font-semibold">{character.wisdom}</div>
                  </div>
                </div>
              </div>

              {/* Hybrid Dragon Info */}
              {character.hybridTribes && (
                <div>
                  <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                    Hybrid Heritage
                  </h3>
                  <div className="bg-gradient-to-r from-purple-900/30 to-blue-900/30 rounded-lg p-3 border border-purple-500/20">
                    <div className="space-y-2 text-sm">
                      {getHybridPowerDescription(character).map((desc, index) => (
                        <p key={index} className="text-slate-300">{desc}</p>
                      ))}
                    </div>
                  </div>
                </div>
              )}

              {/* Traits */}
              <div>
                <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                  Traits
                </h3>
                <div className="flex flex-wrap gap-2">
                  {character.traits.map((trait, index) => (
                    <Badge
                      key={index}
                      variant={trait === 'Corrupted' ? 'destructive' : 'secondary'}
                      className={trait === 'Corrupted' ? 'bg-red-600/30 text-red-300' : 'bg-purple-600/30 text-purple-300'}
                    >
                      {trait}
                    </Badge>
                  ))}
                </div>
              </div>

              {/* Achievements */}
              {character.achievements.length > 0 && (
                <div>
                  <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                    Achievements
                  </h3>
                  <div className="space-y-1">
                    {character.achievements.slice(0, 5).map((achievement, index) => (
                      <Badge key={index} variant="outline" className="w-full justify-start text-xs py-1">
                        <Sparkles className="w-3 h-3 mr-1" />
                        {achievement}
                      </Badge>
                    ))}
                    {character.achievements.length > 5 && (
                      <p className="text-xs text-slate-400">+{character.achievements.length - 5} more...</p>
                    )}
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === "family" && (
            <div className="space-y-4">
              {/* Parents & Siblings */}
              <div>
                <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                  Birth Family
                </h3>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-slate-400">Mother:</span>
                    <span>{character.mother}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-slate-400">Father:</span>
                    <span>{character.father}</span>
                  </div>
                  {character.siblings.length > 0 && (
                    <div className="flex justify-between">
                      <span className="text-slate-400">Siblings:</span>
                      <span>{character.siblings.join(", ")}</span>
                    </div>
                  )}
                </div>
              </div>

              {/* Mate & Romance */}
              {character.mate && (
                <div>
                  <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                    <Heart className="w-4 h-4 inline mr-2" />
                    Romantic Life
                  </h3>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-slate-400">Mate:</span>
                      <span className="text-pink-300">{character.mate}</span>
                    </div>
                  </div>
                </div>
              )}

              {/* Dragonets */}
              {character.dragonets.length > 0 && (
                <div>
                  <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                    <Baby className="w-4 h-4 inline mr-2" />
                    Dragonets ({character.dragonets.length})
                  </h3>
                  <div className="space-y-3">
                    {character.dragonets.map((dragonet, index) => (
                      <div key={index} className="bg-black/30 rounded-lg p-3">
                        <div className="flex justify-between items-center mb-1">
                          <span className="font-semibold text-purple-300">{dragonet.name}</span>
                          <Badge variant="outline" className="text-xs">
                            {dragonet.age} years
                          </Badge>
                        </div>
                        <div className="text-xs space-y-1 text-slate-400">
                          <div>Heritage: {dragonet.hybridTribes ? dragonet.hybridTribes.join('/') : dragonet.tribe}</div>
                          <div>Personality: {dragonet.personality}</div>
                          {dragonet.isAnimus && (
                            <Badge variant="destructive" className="text-xs">
                              <Zap className="w-2 h-2 mr-1" />
                              Animus
                            </Badge>
                          )}
                          {dragonet.inheritedTraits.length > 0 && (
                            <div>Traits: {dragonet.inheritedTraits.join(', ')}</div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === "social" && (
            <div className="space-y-4">
              <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                <Users className="w-4 h-4 inline mr-2" />
                Relationships ({Object.keys(character.relationships).length})
              </h3>
              {Object.keys(character.relationships).length === 0 ? (
                <p className="text-slate-400 text-sm text-center py-4">No significant relationships yet.</p>
              ) : (
                <div className="space-y-2">
                  {Object.values(character.relationships).slice(0, 8).map((relationship, index) => (
                    <div key={index} className="bg-black/30 rounded-lg p-3">
                      <div className="flex justify-between items-center mb-1">
                        <span className="font-semibold text-purple-300">{relationship.name}</span>
                        <Badge 
                          variant={relationship.type === 'mate' || relationship.type === 'romantic' ? 'default' : relationship.type === 'friend' ? 'secondary' : relationship.type === 'rival' || relationship.type === 'enemy' ? 'destructive' : 'outline'}
                          className="text-xs"
                        >
                          {relationship.type === 'mate' ? 'üíï Mate' : relationship.type === 'romantic' ? 'üíñ Romance' : relationship.type === 'friend' ? 'ü§ù Friend' : relationship.type === 'rival' ? '‚öîÔ∏è Rival' : relationship.type === 'enemy' ? 'üíÄ Enemy' : 'ü§∑ Neutral'}
                        </Badge>
                      </div>
                      <div className="text-xs text-slate-400">
                        <div>Strength: {relationship.strength}</div>
                        {relationship.history.length > 0 && (
                          <div>Last: {relationship.history[relationship.history.length - 1]}</div>
                        )}
                      </div>
                    </div>
                  ))}
                  {Object.keys(character.relationships).length > 8 && (
                    <p className="text-xs text-slate-400 text-center">+{Object.keys(character.relationships).length - 8} more relationships...</p>
                  )}
                </div>
              )}
            </div>
          )}

          {activeTab === "inventory" && (
            <div className="space-y-4">
              <h3 className="font-fantasy text-lg font-semibold text-purple-300 mb-3">
                <Package className="w-4 h-4 inline mr-2" />
                Inventory ({inventory.length})
              </h3>
              {inventory.length === 0 ? (
                <p className="text-slate-400 text-sm text-center py-4">No items in inventory.</p>
              ) : (
                <div className="space-y-2">
                  {inventory.slice(0, 6).map((item, index) => (
                    <div key={index} className="bg-black/30 rounded-lg p-3 hover:bg-black/40 transition-colors">
                      <div className="flex justify-between items-center mb-1">
                        <span className="font-semibold text-purple-300">{item.name}</span>
                        <div className="flex items-center gap-2">
                          <Badge variant={item.type === 'enchanted_object' ? 'default' : item.type === 'magical_artifact' ? 'destructive' : 'secondary'} className="text-xs">
                            {item.type.replace('_', ' ')}
                          </Badge>
                          {onUseInventoryItem && (
                            <Button
                              size="sm"
                              variant="ghost"
                              onClick={() => onUseInventoryItem(item)}
                              className="h-6 px-2 text-xs hover:bg-purple-600/30"
                            >
                              Use
                            </Button>
                          )}
                        </div>
                      </div>
                      <p className="text-xs text-slate-300 mb-2">{item.description}</p>
                      {item.enchantments.length > 0 && (
                        <div className="text-xs text-blue-300">
                          ‚ú® {item.enchantments.join(', ')}
                        </div>
                      )}
                      {item.soulCostToCreate && (
                        <div className="text-xs text-red-300 mt-1">
                          Soul cost: {item.soulCostToCreate}%
                        </div>
                      )}
                    </div>
                  ))}
                  {inventory.length > 6 && (
                    <p className="text-xs text-slate-400 text-center">+{inventory.length - 6} more items...</p>
                  )}
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/enhanced-magic-mod.content:
import { useState } from "react";
import { Character } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Wand2, Skull, Zap, Shield, Cloud, Heart, Sword, Eye } from "lucide-react";
import { ENHANCED_MAGIC_SPELLS, calculateSpellSoulCost } from "@/lib/enhanced-magic-system";

interface EnhancedMagicModalProps {
  isOpen: boolean;
  onClose: () => void;
  character: Character;
  onCastSpell?: (spellName: string, soulCost: number) => void;
}

const typeIcons = {
  enchantment: Wand2,
  combat: Sword,
  healing: Heart,
  weather: Cloud,
  curse: Skull,
  summoning: Eye
};

const typeColors = {
  enchantment: "bg-purple-600",
  combat: "bg-red-600", 
  healing: "bg-green-600",
  weather: "bg-blue-600",
  curse: "bg-black",
  summoning: "bg-orange-600"
};

export default function EnhancedMagicModal({ 
  isOpen, 
  onClose, 
  character,
  onCastSpell 
}: EnhancedMagicModalProps) {
  const [selectedType, setSelectedType] = useState<string>("enchantment");

  if (!character.isAnimus) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-md bg-black/90 border-purple-500/30">
          <DialogHeader>
            <DialogTitle className="text-purple-300">Magic Denied</DialogTitle>
            <DialogDescription className="text-purple-200">
              Only animus dragons can cast soul magic. Your character lacks this rare and dangerous gift.
            </DialogDescription>
          </DialogHeader>
          <Button onClick={onClose} className="bg-purple-600 hover:bg-purple-700">
            Close
          </Button>
        </DialogContent>
      </Dialog>
    );
  }

  const spellsByType = ENHANCED_MAGIC_SPELLS.reduce((acc, spell) => {
    if (!acc[spell.type]) acc[spell.type] = [];
    acc[spell.type].push(spell);
    return acc;
  }, {} as Record<string, typeof ENHANCED_MAGIC_SPELLS>);

  const handleCastSpell = (spellName: string) => {
    const spell = ENHANCED_MAGIC_SPELLS.find(s => s.name === spellName);
    if (spell && onCastSpell) {
      const soulCost = calculateSpellSoulCost(spell);
      onCastSpell(spellName, soulCost);
    }
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto bg-black/90 border-purple-500/30">
        <DialogHeader>
          <DialogTitle className="font-fantasy text-2xl text-purple-300 flex items-center">
            <Zap className="w-6 h-6 mr-2 text-red-400" />
            Animus Magic Grimoire
          </DialogTitle>
          <DialogDescription className="text-purple-200">
            Channel your soul's power to reshape reality itself. Each spell costs a fragment of your very essence.
          </DialogDescription>
        </DialogHeader>

        {/* Soul Status Warning */}
        <div className="bg-red-900/30 border border-red-500/50 rounded-lg p-4 mb-4">
          <div className="flex items-center mb-2">
            <Skull className="w-5 h-5 mr-2 text-red-400" />
            <span className="font-semibold text-red-300">Soul Integrity: {character.soulPercentage}%</span>
          </div>
          <p className="text-sm text-red-200">
            Corruption Stage: <Badge variant="destructive">{character.soulCorruptionStage}</Badge>
          </p>
          {character.soulPercentage < 50 && (
            <p className="text-xs text-red-300 mt-2">
              ‚ö†Ô∏è Your soul is heavily corrupted. Magic use is increasingly dangerous.
            </p>
          )}
        </div>

        <Tabs value={selectedType} onValueChange={setSelectedType}>
          <TabsList className="grid w-full grid-cols-6 bg-black/50">
            {Object.keys(spellsByType).map(type => {
              const Icon = typeIcons[type as keyof typeof typeIcons];
              return (
                <TabsTrigger 
                  key={type} 
                  value={type}
                  className="flex flex-col items-center p-2 data-[state=active]:bg-purple-600"
                >
                  <Icon className="w-4 h-4 mb-1" />
                  <span className="text-xs capitalize">{type}</span>
                </TabsTrigger>
              );
            })}
          </TabsList>

          {Object.entries(spellsByType).map(([type, spells]) => (
            <TabsContent key={type} value={type} className="space-y-4">
              <div className="grid gap-4">
                {spells.map((spell) => {
                  const Icon = typeIcons[spell.type as keyof typeof typeIcons];
                  const canCast = character.soulPercentage >= spell.soulCost[0];

                  return (
                    <Card 
                      key={spell.name}
                      className={`bg-black/40 border-purple-500/30 ${
                        !canCast ? 'opacity-50' : 'hover:bg-purple-500/10'
                      }`}
                    >
                      <CardHeader className="pb-3">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center">
                            <div className={`p-2 rounded-full mr-3 ${typeColors[spell.type as keyof typeof typeColors]}`}>
                              <Icon className="w-4 h-4 text-white" />
                            </div>
                            <div>
                              <CardTitle className="text-lg text-purple-300">
                                {spell.name}
                              </CardTitle>
                              <div className="flex items-center space-x-2 mt-1">
                                <Badge 
                                  variant={spell.category === 'catastrophic' ? 'destructive' : 'secondary'}
                                  className="text-xs"
                                >
                                  {spell.category}
                                </Badge>
                                <span className="text-sm text-slate-400">
                                  Soul Cost: {spell.soulCost[0]}-{spell.soulCost[1]}%
                                </span>
                              </div>
                            </div>
                          </div>

                          <Button
                            onClick={() => handleCastSpell(spell.name)}
                            disabled={!canCast}
                            variant={spell.category === 'catastrophic' ? 'destructive' : 'default'}
                            size="sm"
                          >
                            Cast Spell
                          </Button>
                        </div>
                      </CardHeader>

                      <CardContent>
                        <p className="text-purple-200 mb-3">{spell.description}</p>

                        <div className="space-y-2">
                          <h4 className="font-semibold text-purple-300 text-sm">Possible Effects:</h4>
                          <ul className="space-y-1">
                            {spell.examples.map((example, index) => (
                              <li key={index} className="text-sm text-slate-300 flex items-start">
                                <span className="text-purple-400 mr-2">‚Ä¢</span>
                                {example}
                              </li>
                            ))}
                          </ul>
                        </div>

                        {spell.category === 'catastrophic' && (
                          <div className="mt-3 p-2 bg-red-900/30 border border-red-500/50 rounded">
                            <p className="text-xs text-red-300 flex items-center">
                              <Skull className="w-3 h-3 mr-1" />
                              WARNING: Catastrophic magic may completely corrupt your soul
                            </p>
                          </div>
                        )}
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            </TabsContent>
          ))}
        </Tabs>

        <div className="mt-6 p-4 bg-slate-800/50 rounded-lg border border-slate-600">
          <h4 className="font-semibold text-purple-300 mb-2">Remember:</h4>
          <ul className="text-sm text-slate-300 space-y-1">
            <li>‚Ä¢ Each spell permanently removes soul fragments</li>
            <li>‚Ä¢ Higher-tier magic carries exponentially greater risks</li>
            <li>‚Ä¢ Soul corruption changes your personality and choices</li>
            <li>‚Ä¢ At 0% soul, the AI takes complete control of your character</li>
          </ul>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/gameplay-area.tsx.content:
import { Character, GameData, Choice } from "@shared/schema";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Wand2, Package, SkipForward, Eye, Brain, Sparkles, Zap } from "lucide-react";

interface GameplayAreaProps {
  character: Character;
  gameData: GameData;
  onChoice: (choice: Choice) => void;
  onShowMagic: () => void;
  onShowSpecialPower: (powerType: 'prophecy' | 'mindreading' | 'future') => void;
  onShowTribalPowers: () => void;
  onCustomAction: () => void;
  isProcessing: boolean;
}

export default function GameplayArea({
  character,
  gameData,
  onChoice,
  onShowMagic,
  onShowSpecialPower,
  onShowTribalPowers,
  onCustomAction,
  isProcessing,
}: GameplayAreaProps) {
  const scenario = gameData.currentScenario;

  const getChoiceButtonColor = (choice: Choice, index: number) => {
    if (choice.corruption) {
      return "bg-red-900/50 hover:bg-red-900/70 border-red-600/30 text-red-300";
    }
    if (choice.soulCost > 10) {
      return "bg-orange-900/50 hover:bg-orange-900/70 border-orange-600/30";
    }
    if (choice.soulCost > 0) {
      return "bg-yellow-900/50 hover:bg-yellow-900/70 border-yellow-600/30";
    }
    return "bg-black/50 hover:bg-purple-500/20 border-purple-500/30";
  };

  return (
    <div className="lg:col-span-2">
      <Card className="bg-black/40 backdrop-blur-sm border-purple-500/30 h-full flex flex-col">
        {/* Header */}
        <div className="border-b border-purple-500/30 p-4">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="font-fantasy text-xl font-semibold text-purple-300">
                {gameData.location}
              </h3>
              <p className="text-sm text-purple-200">
                Day {Math.floor(gameData.turn / 3)} ‚Ä¢ {gameData.timeInfo}
              </p>
            </div>
            <div className="text-right">
              <div className="text-sm text-slate-400">Turn</div>
              <div className="font-bold text-lg">{gameData.turn}</div>
            </div>
          </div>
        </div>

        {/* Narrative Display */}
        <div className="flex-1 p-6 overflow-y-auto">
          <div className="narrative-text font-narrative text-base leading-relaxed">
            {scenario.narrativeText.map((paragraph, index) => (
              <p
                key={index}
                className="mb-4"
                dangerouslySetInnerHTML={{
                  __html: paragraph
                    .replace(/\*([^*]+)\*/g, '<em>$1</em>')
                    .replace(/\*\*([^*]+)\*\*/g, '<strong>$1</strong>')
                }}
              />
            ))}

            {scenario.type === 'magical' && (
              <div className="bg-black/50 border-l-4 border-purple-400 p-4 rounded-r-lg mb-6">
                <p className="font-semibold text-purple-300 mb-2">Current Scenario:</p>
                <p>{scenario.description}</p>
              </div>
            )}
          </div>
        </div>

        {/* Choice Selection Area */}
        <div className="border-t border-purple-500/30 p-6">
          {character.isAIControlled ? (
            <div className="bg-red-900/50 border border-red-500/50 rounded-lg p-4 mb-4">
              <h4 className="font-fantasy text-lg font-semibold text-red-300 mb-2 flex items-center">
                <Eye className="w-5 h-5 mr-2 animate-pulse" />
                AI Controlling Your Dragon
              </h4>
              <p className="text-red-200 text-sm">
                Your corrupted dragon is now under AI control. Watch as evil choices are made automatically...
              </p>
            </div>
          ) : (
            <h4 className="font-fantasy text-lg font-semibold text-purple-300 mb-4">
              Choose your action:
            </h4>
          )}

          <div className="space-y-3">
            {scenario.choices.map((choice, index) => (
              <Button
                key={choice.id}
                variant="ghost"
                className={`choice-button w-full text-left p-4 border transition-all duration-300 ${getChoiceButtonColor(choice, index)}`}
                onClick={() => onChoice(choice)}
                disabled={isProcessing || character.isAIControlled}
              >
                <div className="flex items-start">
                  <div className="bg-purple-400 text-black rounded-full w-6 h-6 flex items-center justify-center text-sm font-bold mr-3 mt-1">
                    {index + 1}
                  </div>
                  <div className="flex-1">
                    <div className="font-medium text-slate-200">{choice.text}</div>
                    <div className="text-sm text-slate-400 mt-1">{choice.description}</div>
                  </div>
                </div>
              </Button>
            ))}
          </div>

          {/* Quick Actions Bar */}
          {!character.isAIControlled && (
            <div className="mt-6 pt-4 border-t border-purple-500/20">
              <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
              {/* Animus Magic - Only for animus dragons */}
              {character.isAnimus && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={onShowMagic}
                  className="border-red-500/50 text-red-400 hover:bg-red-500/10"
                >
                  <Wand2 className="w-4 h-4 mr-1" />
                  Animus Magic
                </Button>
              )}

              {/* Special Powers - Prophecy, Mind Reading, Future Sight */}
              {character.tribalPowers.some(power => power.toLowerCase().includes('prophecy') || power.toLowerCase().includes('future')) && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onShowSpecialPower('prophecy')}
                  className="border-blue-500/50 text-blue-400 hover:bg-blue-500/10"
                >
                  <Eye className="w-4 h-4 mr-1" />
                  Prophecy
                </Button>
              )}

              {character.tribalPowers.some(power => power.toLowerCase().includes('mind')) && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onShowSpecialPower('mindreading')}
                  className="border-purple-500/50 text-purple-400 hover:bg-purple-500/10"
                >
                  <Brain className="w-4 h-4 mr-1" />
                  Mind Reading
                </Button>
              )}

              {character.specialPowers.some(power => power.toLowerCase().includes('foresight') || power.toLowerCase().includes('future')) && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => onShowSpecialPower('future')}
                  className="border-cyan-500/50 text-cyan-400 hover:bg-cyan-500/10"
                >
                  <Sparkles className="w-4 h-4 mr-1" />
                  Future Sight
                </Button>
              )}

              {/* Tribal Powers */}
              <Button
                variant="outline"
                size="sm"
                onClick={onShowTribalPowers}
                className="border-green-500/50 text-green-400 hover:bg-green-500/10"
              >
                <Zap className="w-4 h-4 mr-1" />
                Tribal Powers
              </Button>

              {/* Custom Action */}
              <Button
                variant="outline"
                size="sm"
                onClick={onCustomAction}
                className="border-yellow-500/50 text-yellow-400 hover:bg-yellow-500/10"
              >
                <Package className="w-4 h-4 mr-1" />
                Custom Action
              </Button>
            </div>
            </div>
          )}
        </div>
      </Card>
    </div>
  );
}

client/src/components/magic-modal.tsx.content:
import { Character, MagicSpell, CustomSpell, InventoryItem } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Wand2, Skull, Zap, Shield, Sparkles, Package } from "lucide-react";
import { useState } from "react";
import { ENHANCED_MAGIC_SPELLS, calculateSpellSoulCost } from "@/lib/enhanced-magic-system";

interface MagicModalProps {
  isOpen: boolean;
  onClose: () => void;
  character: Character;
  inventory: InventoryItem[];
  onCastSpell: (spell: CustomSpell) => void;
}


export default function MagicModal({ isOpen, onClose, character, inventory, onCastSpell }: MagicModalProps) {
  const [activeTab, setActiveTab] = useState<"predefined" | "custom" | "inventory">("custom");
  const [customSpell, setCustomSpell] = useState({
    targetObject: "",
    enchantmentDescription: "",
    spellType: "enchantment" as const
  });

  const estimateSpellCost = () => {
    if (!customSpell.targetObject || !customSpell.enchantmentDescription) return 0;

    const words = customSpell.enchantmentDescription.split(' ').length;
    const complexity = words > 20 ? "catastrophic" : words > 10 ? "complex" : words > 5 ? "moderate" : "simple";

    const baseCost = {
      simple: [1, 5],
      moderate: [5, 15],
      complex: [15, 30],
      catastrophic: [30, 60]
    }[complexity];

    const typeMultiplier = {
      enchantment: 1,
      healing: 0.8,
      weather: 1.2,
      combat: 1.1,
      summoning: 1.3,
      curse: 1.4
    }[customSpell.spellType];

    return Math.floor(baseCost[1] * typeMultiplier);
  };

  const handleCastCustomSpell = () => {
    if (!customSpell.targetObject || !customSpell.enchantmentDescription) return;

    const spell: CustomSpell = {
      id: `custom_${Date.now()}`,
      targetObject: customSpell.targetObject,
      enchantmentDescription: customSpell.enchantmentDescription,
      estimatedSoulCost: estimateSpellCost(),
      spellType: customSpell.spellType,
      complexity: estimateSpellCost() > 30 ? "catastrophic" : estimateSpellCost() > 15 ? "complex" : estimateSpellCost() > 5 ? "moderate" : "simple",
      turnCast: Date.now()
    };

    onCastSpell(spell);
    setCustomSpell({ targetObject: "", enchantmentDescription: "", spellType: "enchantment" });
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'minor':
        return 'border-green-500/30 bg-green-500/10';
      case 'moderate':
        return 'border-yellow-500/30 bg-yellow-500/10';
      case 'major':
        return 'border-orange-500/30 bg-orange-500/10';
      case 'catastrophic':
        return 'border-red-500/30 bg-red-500/10';
      default:
        return 'border-gray-500/30 bg-gray-500/10';
    }
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'minor':
        return <Shield className="w-5 h-5" />;
      case 'moderate':
        return <Zap className="w-5 h-5" />;
      case 'major':
        return <Sparkles className="w-5 h-5" />;
      case 'catastrophic':
        return <Skull className="w-5 h-5" />;
      default:
        return <Wand2 className="w-5 h-5" />;
    }
  };

  const getCorruptionInfo = () => {
    const stage = character.soulCorruptionStage;
    switch (stage) {
      case 'Normal':
        return { text: "Full control", color: "text-green-400" };
      case 'Frayed':
        return { text: "Occasional dark thoughts", color: "text-yellow-400" };
      case 'Twisted':
        return { text: "Partial AI control", color: "text-orange-400" };
      case 'Broken':
        return { text: "Mostly corrupted", color: "text-red-400" };
    }
  };

  const corruptionInfo = getCorruptionInfo();

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[80vh] bg-black/90 border-purple-500/50 text-slate-100 overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="font-fantasy text-2xl font-bold text-purple-300 flex items-center">
            <Wand2 className="mr-3" />
            Animus Magic Spells
          </DialogTitle>
          <DialogDescription className="text-purple-200">
            Understanding the cost of power. Each spell category represents increasing corruption to your soul.
          </DialogDescription>
        </DialogHeader>

        {/* Tab Navigation */}
        <div className="flex space-x-4 mb-6">
          <Button 
            variant={activeTab === "custom" ? "default" : "outline"}
            onClick={() => setActiveTab("custom")}
            className="flex items-center gap-2"
          >
            <Sparkles className="w-4 h-4" />
            Cast Custom Spell
          </Button>
          <Button 
            variant={activeTab === "predefined" ? "default" : "outline"}
            onClick={() => setActiveTab("predefined")}
            className="flex items-center gap-2"
          >
            <Wand2 className="w-4 h-4" />
            Spell Examples
          </Button>
          <Button 
            variant={activeTab === "inventory" ? "default" : "outline"}
            onClick={() => setActiveTab("inventory")}
            className="flex items-center gap-2"
          >
            <Package className="w-4 h-4" />
            Inventory ({inventory.length})
          </Button>
        </div>

        {/* Custom Spell Casting */}
        {activeTab === "custom" && (
          <div className="space-y-6">
            <Card className="border-purple-500/30 bg-purple-500/10">
              <CardHeader>
                <CardTitle className="text-purple-300">Create Your Own Animus Spell</CardTitle>
                <DialogDescription className="text-purple-200">
                  Describe what object you want to enchant and what you want it to do. Be creative!
                </DialogDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="target-object" className="text-slate-300">What do you want to enchant?</Label>
                  <Input
                    id="target-object"
                    placeholder="e.g., a sword, a ring, a cloak, the weather, an enemy..."
                    value={customSpell.targetObject}
                    onChange={(e) => setCustomSpell({...customSpell, targetObject: e.target.value})}
                    className="bg-black/50 border-purple-400/30 text-slate-100"
                  />
                </div>

                <div>
                  <Label htmlFor="enchantment" className="text-slate-300">Describe the enchantment:</Label>
                  <Input
                    id="enchantment"
                    placeholder="e.g., to cut through any material, to make me invisible, to summon lightning..."
                    value={customSpell.enchantmentDescription}
                    onChange={(e) => setCustomSpell({...customSpell, enchantmentDescription: e.target.value})}
                    className="bg-black/50 border-purple-400/30 text-slate-100"
                  />
                </div>

                <div>
                  <Label htmlFor="spell-type" className="text-slate-300">Spell Type:</Label>
                  <Select value={customSpell.spellType} onValueChange={(value: any) => setCustomSpell({...customSpell, spellType: value})}>
                    <SelectTrigger className="bg-black/50 border-purple-400/30 text-slate-100">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="enchantment">Enchantment</SelectItem>
                      <SelectItem value="combat">Combat</SelectItem>
                      <SelectItem value="healing">Healing</SelectItem>
                      <SelectItem value="weather">Weather</SelectItem>
                      <SelectItem value="curse">Curse</SelectItem>
                      <SelectItem value="summoning">Summoning</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="bg-black/30 p-4 rounded border border-yellow-400/30">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-yellow-300 font-semibold">Estimated Soul Cost:</span>
                    <span className="text-yellow-300 font-bold text-lg">{estimateSpellCost()}%</span>
                  </div>
                  <p className="text-xs text-yellow-200">
                    More complex descriptions and dangerous spell types cost more soul.
                  </p>
                </div>

                <Button 
                  onClick={handleCastCustomSpell}
                  disabled={!customSpell.targetObject || !customSpell.enchantmentDescription || character.soulPercentage < estimateSpellCost()}
                  className="w-full bg-purple-600 hover:bg-purple-700"
                >
                  <Sparkles className="w-4 h-4 mr-2" />
                  Cast Spell ({estimateSpellCost()}% soul cost)
                </Button>
              </CardContent>
            </Card>

            {/* Soul Status */}
            <Card className="border-purple-500/30 bg-purple-500/10">
              <CardHeader className="pb-3">
                <CardTitle className="flex items-center gap-2 text-lg text-purple-400">
                  <Skull />
                  Your Soul Status
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  <div className="flex justify-between items-center">
                    <span className="text-slate-300">Current Purity:</span>
                    <span className="font-bold">{character.soulPercentage}%</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-slate-300">Corruption Stage:</span>
                    <span className={`font-semibold ${corruptionInfo.color}`}>
                      {character.soulCorruptionStage}
                    </span>
                  </div>
                  <div className="text-xs text-slate-400 mt-3 space-y-1">
                    <div>‚Ä¢ Normal (75-100%): Full control over choices</div>
                    <div>‚Ä¢ Frayed (50-74%): Occasional dark thoughts</div>
                    <div>‚Ä¢ Twisted (25-49%): AI may override decisions</div>
                    <div>‚Ä¢ Broken (0-24%): Mostly AI controlled</div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Predefined Spell Examples */}
        {activeTab === "predefined" && (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {ENHANCED_MAGIC_SPELLS.slice(0, 6).map((spell) => (
              <Card key={spell.name} className={`${getCategoryColor(spell.category)} border`}>
                <CardHeader className="pb-3">
                  <CardTitle className="flex items-center gap-2 text-lg">
                    {getCategoryIcon(spell.category)}
                    {spell.name}
                    <Badge
                      variant="outline"
                      className={`ml-auto ${
                        spell.category === 'minor'
                          ? 'border-green-400 text-green-400'
                          : spell.category === 'moderate'
                          ? 'border-yellow-400 text-yellow-400'
                          : spell.category === 'major'
                          ? 'border-orange-400 text-orange-400'
                          : 'border-red-400 text-red-400'
                      }`}
                    >
                      {spell.soulCost[0]}-{spell.soulCost[1]}% soul
                    </Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <p className="text-sm text-slate-300 mb-3">{spell.description}</p>
                  <ul className="text-sm space-y-1 text-slate-300">
                    {spell.examples.slice(0, 3).map((example, index) => (
                      <li key={index}>‚Ä¢ {example}</li>
                    ))}
                  </ul>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Inventory */}
        {activeTab === "inventory" && (
          <div className="space-y-4">
            {inventory.length === 0 ? (
              <Card className="border-gray-500/30 bg-gray-500/10">
                <CardContent className="pt-6 text-center">
                  <Package className="w-12 h-12 mx-auto mb-4 text-gray-400" />
                  <p className="text-slate-300">Your inventory is empty.</p>
                  <p className="text-slate-400 text-sm">Create enchanted objects to add them to your inventory.</p>
                </CardContent>
              </Card>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {inventory.map((item) => (
                  <Card key={item.id} className="border-blue-500/30 bg-blue-500/10">
                    <CardHeader className="pb-3">
                      <CardTitle className="flex items-center gap-2 text-lg text-blue-300">
                        <Package className="w-5 h-5" />
                        {item.name}
                        <Badge variant="outline" className="ml-auto border-blue-400 text-blue-400">
                          {item.type.replace('_', ' ')}
                        </Badge>
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <p className="text-sm text-slate-300 mb-3">{item.description}</p>
                      {item.enchantments.length > 0 && (
                        <div>
                          <p className="text-xs text-blue-300 font-semibold mb-1">Enchantments:</p>
                          <ul className="text-xs space-y-1 text-slate-300">
                            {item.enchantments.map((enchantment, index) => (
                              <li key={index}>‚Ä¢ {enchantment}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                      {item.soulCostToCreate && (
                        <p className="text-xs text-slate-400 mt-2">
                          Soul cost to create: {item.soulCostToCreate}%
                        </p>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        )}

        <div className="bg-black/50 border border-purple-400/30 rounded-lg p-4">
          <h4 className="font-semibold text-purple-300 mb-2 flex items-center">
            <Skull className="w-4 h-4 mr-2" />
            Warning: The Price of Power
          </h4>
          <p className="text-sm text-purple-200">
            Every use of animus magic corrupts your soul. As your purity decreases, you will lose control
            over your choices. The game will begin making increasingly dark decisions for you. At 0% purity,
            you become a villain that others must stop.
          </p>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/sanity-bar.tsx.content:
import { Progress } from "@/components/ui/progress";
import { Brain, AlertTriangle } from "lucide-react";

interface SanityBarProps {
  sanityPercentage: number;
  className?: string;
}

export default function SanityBar({ sanityPercentage, className = "" }: SanityBarProps) {
  const getSanityColor = (sanity: number) => {
    if (sanity >= 70) return "bg-blue-500";
    if (sanity >= 50) return "bg-yellow-500";
    if (sanity >= 30) return "bg-orange-500";
    return "bg-red-500";
  };

  const getSanityLabel = (sanity: number) => {
    if (sanity >= 70) return "Stable";
    if (sanity >= 50) return "Stressed";
    if (sanity >= 30) return "Unstable";
    if (sanity >= 10) return "Fragmented";
    return "Breaking";
  };

  const getSanityDescription = (sanity: number) => {
    if (sanity >= 70) return "Your mind is clear and focused.";
    if (sanity >= 50) return "You feel the weight of knowledge and power.";
    if (sanity >= 30) return "Reality seems less certain than before.";
    if (sanity >= 10) return "You struggle to distinguish truth from delusion.";
    return "Your grip on reality is slipping away.";
  };

  return (
    <div className={`space-y-2 ${className}`}>
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Brain className="w-4 h-4 text-blue-400" />
          <span className="text-sm font-medium text-purple-200">
            Sanity: {getSanityLabel(sanityPercentage)}
          </span>
        </div>
        <span className="text-sm text-purple-300">
          {Math.round(sanityPercentage)}%
        </span>
      </div>

      <div className="relative">
        <Progress 
          value={sanityPercentage} 
          className="h-3 bg-purple-900/50" 
        />
        <div 
          className={`absolute top-0 left-0 h-3 rounded-full transition-all duration-300 ${getSanityColor(sanityPercentage)}`}
          style={{ width: `${sanityPercentage}%` }}
        />
      </div>

      <p className="text-xs text-purple-300">
        {getSanityDescription(sanityPercentage)}
      </p>

      {sanityPercentage <= 20 && (
        <div className="flex items-center gap-2 text-red-400 text-xs">
          <AlertTriangle className="w-3 h-3" />
          <span>Warning: Mental breakdown imminent!</span>
        </div>
      )}
    </div>
  );
}

client/src/components/tribal-powers-moda.content:
import { Character } from "@shared/schema";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { MockAIService } from "@/lib/mock-ai-service";
import { Zap, Eye, Flame, Droplets, Snowflake, Leaf, Moon, Sparkles } from "lucide-react";
import { useState } from "react";

interface TribalPowersModalProps {
  character: Character;
  currentScenario?: string;
  isOpen: boolean;
  onClose: () => void;
  onUsePower: (power: string, scenario?: string) => void;
}

const POWER_ICONS = {
  "Fire resistance": Flame,
  "Mud camouflage": Eye,
  "Enhanced strength when warm": Zap,
  "Venomous tail barb": Zap,
  "Desert survival": Flame,
  "Heat resistance": Flame,
  "Superior flight speed": Sparkles,
  "Fire breathing": Flame,
  "High altitude adaptation": Sparkles,
  "Underwater breathing": Droplets,
  "Bioluminescent scales": Sparkles,
  "Deep sea pressure resistance": Droplets,
  "Frost breath": Snowflake,
  "Cold immunity": Snowflake,
  "Serrated claws": Zap,
  "Color-changing scales": Sparkles,
  "Acidic venom": Zap,
  "Prehensile tail": Zap,
  "Future sight": Moon,
  "Mind reading": Eye,
  "Silk production": Sparkles,
  "Metamorphosis": Sparkles,
  "Enhanced agility": Zap,
  "Paralytic stinger": Zap,
  "Hive mind connection": Eye,
  "Toxic breath": Zap,
  "Plant manipulation": Leaf,
  "Leaf speak": Leaf,
  "Photosynthesis healing": Leaf,
};

export default function TribalPowersModal({ character, currentScenario, isOpen, onClose, onUsePower }: TribalPowersModalProps) {
  const [selectedPower, setSelectedPower] = useState<string | null>(null);
  const [powerScenarios, setPowerScenarios] = useState<string[]>([]);
  const [aiResponse, setAiResponse] = useState<string | null>(null);

  const allPowers = [...character.tribalPowers, ...character.specialPowers];

  const getPowerIcon = (power: string) => {
    const IconComponent = POWER_ICONS[power as keyof typeof POWER_ICONS];
    return IconComponent ? <IconComponent className="w-4 h-4" /> : <Zap className="w-4 h-4" />;
  };

  const handlePowerSelect = (power: string) => {
    setSelectedPower(power);
    const powerType = determinePowerType(power);
    const scenarios = MockAIService.generatePowerUsageOptions(powerType, { 
      turn: Math.floor(Math.random() * 100),
      currentSituation: currentScenario || "general situation",
      powerName: power
    });
    setPowerScenarios(scenarios);
    setAiResponse(null);
  };

  const handleUsePowerWithScenario = (scenario: string) => {
    const response = MockAIService.generateRandomEvent(character, { location: currentScenario });
    setAiResponse(response.content);
    onUsePower(selectedPower!, scenario);
  };

  const handleUseVisionPower = (power: string) => {
    if (power.toLowerCase().includes('future') || power.toLowerCase().includes('prophecy')) {
      const prophecy = MockAIService.generateProphecy(character, { turn: Math.random() * 100 });
      setAiResponse(prophecy.content);
    } else if (power.toLowerCase().includes('mind')) {
      const vision = MockAIService.generateVision(character, { turn: Math.random() * 100 });
      setAiResponse(vision.content);
    }
    onUsePower(power);
  };

  const determinePowerType = (power: string): string => {
    const powerLower = power.toLowerCase();
    if (powerLower.includes('fire') || powerLower.includes('flame') || powerLower.includes('breath')) return 'fire';
    if (powerLower.includes('ice') || powerLower.includes('frost') || powerLower.includes('cold')) return 'ice';
    if (powerLower.includes('water') || powerLower.includes('sea') || powerLower.includes('underwater')) return 'water';
    if (powerLower.includes('earth') || powerLower.includes('mud')) return 'earth';
    if (powerLower.includes('electric') || powerLower.includes('lightning')) return 'electricity';
    if (powerLower.includes('mind')) return 'mind_reading';
    if (powerLower.includes('future') || powerLower.includes('prophecy')) return 'prophecy';
    if (powerLower.includes('camouflage') || powerLower.includes('color')) return 'camouflage';
    return 'fire';
  };

  const resetModal = () => {
    setSelectedPower(null);
    setPowerScenarios([]);
    setAiResponse(null);
  };

  return (
    <Dialog open={isOpen} onOpenChange={(open) => { if (!open) { resetModal(); onClose(); } }}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-purple-100">
            {character.hybridTribes ? "Hybrid" : character.tribe} Powers & Abilities
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {!selectedPower ? (
            <>
              {/* Tribal Powers */}
              {character.tribalPowers.length > 0 && (
                <div>
                  <h3 className="text-lg font-semibold text-purple-200 mb-3">Tribal Powers</h3>
                  <div className="space-y-3">
                    {character.tribalPowers.map((power, index) => (
                      <div key={index} className="flex items-center justify-between p-3 bg-purple-900/30 rounded-lg">
                        <div className="flex items-center">
                          {getPowerIcon(power)}
                          <span className="ml-3 font-medium text-purple-100">{power}</span>
                        </div>
                        <div className="flex gap-2">
                          {(power.toLowerCase().includes('future') || power.toLowerCase().includes('mind') || power.toLowerCase().includes('prophecy')) && (
                            <Button
                              size="sm"
                              onClick={() => handleUseVisionPower(power)}
                              className="bg-blue-600 hover:bg-blue-700"
                            >
                              <Eye className="w-3 h-3 mr-1" />
                              Vision
                            </Button>
                          )}
                          <Button
                            size="sm"
                            onClick={() => handlePowerSelect(power)}
                            className="bg-purple-600 hover:bg-purple-700"
                          >
                            <Sparkles className="w-3 h-3 mr-1" />
                            Use
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Special Powers */}
              {character.specialPowers.length > 0 && (
                <div>
                  <h3 className="text-lg font-semibold text-purple-200 mb-3">Special Abilities</h3>
                  <div className="space-y-3">
                    {character.specialPowers.map((power, index) => (
                      <div key={index} className="flex items-center justify-between p-3 bg-blue-900/30 rounded-lg">
                        <div className="flex items-center">
                          {getPowerIcon(power)}
                          <span className="ml-3 font-medium text-blue-100">{power}</span>
                          <Badge variant="outline" className="ml-2 text-xs text-amber-400 border-amber-400">
                            Rare
                          </Badge>
                        </div>
                        <Button
                          size="sm"
                          onClick={() => handlePowerSelect(power)}
                          className="bg-blue-600 hover:bg-blue-700"
                        >
                          <Sparkles className="w-3 h-3 mr-1" />
                          Use
                        </Button>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* No Powers */}
              {allPowers.length === 0 && (
                <div className="text-center py-8 text-purple-300">
                  <p>You have not yet manifested any special powers.</p>
                  <p className="text-sm mt-2">Powers may develop as you grow and face challenges.</p>
                </div>
              )}

              {/* Animus Warning */}
              {character.isAnimus && (
                <div className="mt-6 p-4 bg-red-900/30 border border-red-700 rounded-lg">
                  <p className="text-red-200 text-sm">
                    <strong>Animus Warning:</strong> Your animus magic is far more powerful than these tribal abilities, 
                    but remember - every spell costs part of your soul.
                  </p>
                </div>
              )}
            </>
          ) : (
            /* Power Usage Scenarios */
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <h4 className="font-semibold text-purple-300 flex items-center">
                  {getPowerIcon(selectedPower)}
                  <span className="ml-2">Using: {selectedPower}</span>
                </h4>
                <Button size="sm" variant="ghost" onClick={resetModal}>Back</Button>
              </div>

              <div className="space-y-3">
                <p className="text-sm text-slate-300">Choose how you want to use this power:</p>
                {powerScenarios.map((scenario, index) => (
                  <div 
                    key={index} 
                    className="p-3 bg-black/30 rounded-lg border border-purple-500/30 hover:border-purple-400 cursor-pointer transition-colors"
                    onClick={() => handleUsePowerWithScenario(scenario)}
                  >
                    <p className="text-sm text-slate-200">{scenario}</p>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* AI Response */}
          {aiResponse && (
            <div className="mt-4 p-4 bg-gradient-to-r from-purple-900/50 to-blue-900/50 rounded-lg border border-purple-500/30">
              <div className="flex items-center mb-2">
                <Sparkles className="w-4 h-4 text-purple-400 mr-2" />
                <h4 className="font-semibold text-purple-300">Mystical Vision</h4>
              </div>
              <p className="text-sm text-slate-200 italic">{aiResponse}</p>
            </div>
          )}
        </div>

        <div className="flex justify-end mt-6">
          <Button onClick={() => { resetModal(); onClose(); }} variant="outline">
            Close
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

