import { Character } from "@shared/schema";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { MockAIService } from "@/lib/mock-ai-service";
import { MessageCircle, ArrowRight, X } from "lucide-react";
import { useState, useEffect } from "react";

interface ConversationMessage {
  speaker: string;
  text: string;
  isPlayer: boolean;
}

interface ConversationChoice {
  text: string;
  tone: 'friendly' | 'hostile' | 'neutral' | 'diplomatic' | 'cunning';
}

interface ConversationModalProps {
  character: Character;
  conversationTopic: string;
  otherDragon: string;
  isOpen: boolean;
  onClose: () => void;
  onConversationEnd: (outcome: string, relationship: string) => void;
}

export default function ConversationModal({ 
  character, 
  conversationTopic,
  otherDragon,
  isOpen, 
  onClose, 
  onConversationEnd 
}: ConversationModalProps) {
  const [messages, setMessages] = useState<ConversationMessage[]>([]);
  const [currentChoices, setCurrentChoices] = useState<ConversationChoice[]>([]);
  const [conversationTurn, setConversationTurn] = useState(0);
  const [isGenerating, setIsGenerating] = useState(false);

  useEffect(() => {
    if (isOpen && conversationTopic) {
      initializeConversation();
    }
  }, [isOpen, conversationTopic]);

  const initializeConversation = () => {
    setMessages([]);
    setConversationTurn(0);

    // Generate opening message from the other dragon
    const openingContext = {
      topic: conversationTopic,
      speaker: otherDragon,
      turn: 0,
      playerTribe: character.tribe
    };

    const opening = MockAIService.generateRandomEvent(character, openingContext);

    setMessages([{
      speaker: otherDragon,
      text: opening.content,
      isPlayer: false
    }]);

    generateChoices();
  };

  const generateChoices = () => {
    const choices: ConversationChoice[] = [
      {
        text: generateChoiceText('friendly'),
        tone: 'friendly'
      },
      {
        text: generateChoiceText('neutral'),
        tone: 'neutral'
      },
      {
        text: generateChoiceText('diplomatic'),
        tone: 'diplomatic'
      }
    ];

    // Add hostile or cunning options based on character traits
    if (character.traits.includes('Fierce') || character.traits.includes('Vengeful')) {
      choices.push({
        text: generateChoiceText('hostile'),
        tone: 'hostile'
      });
    }

    if (character.traits.includes('Cunning') || character.traits.includes('Ambitious')) {
      choices.push({
        text: generateChoiceText('cunning'),
        tone: 'cunning'
      });
    }

    setCurrentChoices(choices);
  };

  const generateChoiceText = (tone: string): string => {
    const templates = {
      friendly: [
        "That's interesting! Tell me more about that.",
        "I appreciate you sharing this with me.",
        "I'd love to help if I can.",
        "That sounds like quite an adventure!"
      ],
      neutral: [
        "I see. What do you think we should do?",
        "That's certainly one way to look at it.",
        "Hmm, let me think about that.",
        "What would you suggest?"
      ],
      diplomatic: [
        "Perhaps we could find a solution that works for everyone.",
        "I understand your perspective, but consider this...",
        "Maybe there's a way to compromise here.",
        "Let's think about this carefully before acting."
      ],
      hostile: [
        "That's ridiculous! You're completely wrong.",
        "I don't have time for this nonsense.",
        "You're either lying or completely deluded.",
        "Keep talking and see what happens."
      ],
      cunning: [
        "That's... very convenient for you, isn't it?",
        "I wonder what you're not telling me.",
        "Interesting. And what do you get out of this?",
        "I'm sure there's more to this story."
      ]
    };

    const toneTemplates = templates[tone as keyof typeof templates] || templates.neutral;
    return toneTemplates[Math.floor(Math.random() * toneTemplates.length)];
  };

  const handleChoice = async (choice: ConversationChoice) => {
    setIsGenerating(true);

    // Add player message
    const newMessages = [...messages, {
      speaker: character.name,
      text: choice.text,
      isPlayer: true
    }];

    setMessages(newMessages);

    // Generate response
    await new Promise(resolve => setTimeout(resolve, 1000));

    const responseContext = {
      topic: conversationTopic,
      playerChoice: choice.text,
      playerTone: choice.tone,
      turn: conversationTurn + 1,
      characterTraits: character.traits
    };

    const response = MockAIService.generateRandomEvent(character, responseContext);

    const finalMessages = [...newMessages, {
      speaker: otherDragon,
      text: response.content,
      isPlayer: false
    }];

    setMessages(finalMessages);
    setConversationTurn(prev => prev + 1);

    // End conversation after 3-4 exchanges or generate new choices
    if (conversationTurn >= 2) {
      endConversation(choice.tone);
    } else {
      generateChoices();
    }

    setIsGenerating(false);
  };

  const endConversation = (lastTone: string) => {
    const outcomes = {
      friendly: ["The conversation ends on a positive note", "You've made a new friend", "They seem to trust you more"],
      hostile: ["The conversation escalates into conflict", "They storm off angrily", "You've made an enemy"],
      neutral: ["The conversation ends inconclusively", "You both go your separate ways", "Nothing much changes"],
      diplomatic: ["You reach a mutual understanding", "A compromise is achieved", "Both parties benefit"],
      cunning: ["You learn valuable information", "They suspect your motives", "The situation becomes complex"]
    };

    const outcomeTexts = outcomes[lastTone as keyof typeof outcomes] || outcomes.neutral;
    const outcome = outcomeTexts[Math.floor(Math.random() * outcomeTexts.length)];

    const relationshipChanges = {
      friendly: "improved",
      hostile: "worsened", 
      neutral: "unchanged",
      diplomatic: "strengthened",
      cunning: "complicated"
    };

    const relationship = relationshipChanges[lastTone as keyof typeof relationshipChanges];

    setTimeout(() => {
      onConversationEnd(outcome, relationship);
      onClose();
    }, 2000);
  };

  const getToneColor = (tone: string) => {
    const colors = {
      friendly: "text-green-400 border-green-500/30",
      hostile: "text-red-400 border-red-500/30",
      neutral: "text-slate-400 border-slate-500/30",
      diplomatic: "text-blue-400 border-blue-500/30",
      cunning: "text-purple-400 border-purple-500/30"
    };
    return colors[tone as keyof typeof colors] || colors.neutral;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-blue-300 flex items-center gap-2">
            <MessageCircle className="w-5 h-5" />
            Conversation with {otherDragon}
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div className="p-3 bg-blue-900/20 border border-blue-500/30 rounded-lg">
            <p className="text-sm text-slate-300">Topic: {conversationTopic}</p>
          </div>

          {/* Conversation Messages */}
          <div className="space-y-3 max-h-60 overflow-y-auto">
            {messages.map((message, index) => (
              <div key={index} className={`p-3 rounded-lg ${
                message.isPlayer 
                  ? 'bg-purple-900/30 border border-purple-500/30 ml-4' 
                  : 'bg-slate-900/30 border border-slate-500/30 mr-4'
              }`}>
                <div className="flex items-center gap-2 mb-1">
                  <Badge variant={message.isPlayer ? "default" : "outline"} className="text-xs">
                    {message.speaker}
                  </Badge>
                </div>
                <p className="text-sm text-slate-200">{message.text}</p>
              </div>
            ))}
          </div>

          {/* Conversation Choices */}
          {currentChoices.length > 0 && !isGenerating && (
            <div className="space-y-3">
              <h4 className="font-semibold text-slate-200">How do you respond?</h4>
              {currentChoices.map((choice, index) => (
                <Button
                  key={index}
                  variant="outline"
                  className={`w-full text-left p-3 ${getToneColor(choice.tone)}`}
                  onClick={() => handleChoice(choice)}
                >
                  <div className="flex items-center justify-between">
                    <span className="text-sm">{choice.text}</span>
                    <div className="flex items-center gap-2">
                      <Badge variant="outline" className="text-xs">
                        {choice.tone}
                      </Badge>
                      <ArrowRight className="w-4 h-4" />
                    </div>
                  </div>
                </Button>
              ))}
            </div>
          )}

          {isGenerating && (
            <div className="text-center py-4">
              <MessageCircle className="w-6 h-6 animate-pulse mx-auto mb-2 text-blue-400" />
              <p className="text-sm text-slate-400">They are thinking...</p>
            </div>
          )}

          {conversationTurn >= 3 && (
            <div className="p-3 bg-green-900/20 border border-green-500/30 rounded-lg">
              <p className="text-sm text-green-300">Conversation concluding...</p>
            </div>
          )}
        </div>

        <div className="flex justify-end mt-6">
          <Button onClick={onClose} variant="outline">
            <X className="w-4 h-4 mr-2" />
            End Conversation
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/custom-action-moda.content:
import { Character, GameData, InventoryItem } from "@shared/schema";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { MockAIService } from "@/lib/mock-ai-service";
import { Sparkles, Send, Package, Zap } from "lucide-react";
import { useState } from "react";

interface CustomActionModalProps {
  character: Character;
  gameData: GameData;
  inventory: InventoryItem[];
  isOpen: boolean;
  onClose: () => void;
  onExecuteAction: (action: string, result: string, itemUsed?: InventoryItem) => void;
}

export default function CustomActionModal({ 
  character, 
  gameData,
  inventory, 
  isOpen, 
  onClose, 
  onExecuteAction 
}: CustomActionModalProps) {
  const [customAction, setCustomAction] = useState("");
  const [selectedItem, setSelectedItem] = useState<InventoryItem | null>(null);
  const [actionResult, setActionResult] = useState<string>("");
  const [isGenerating, setIsGenerating] = useState(false);

  const generateActionResult = () => {
    if (!customAction.trim()) return;

    setIsGenerating(true);

    // Simulate AI processing
    setTimeout(() => {
      // Generate AI response based on action and context
      const context = {
        turn: gameData.turn,
        location: gameData.location,
        action: customAction,
        tribe: character.tribe,
        item: selectedItem?.name,
        itemEnchantments: selectedItem?.enchantments || []
      };

      let result = "";

      if (selectedItem) {
        // Action with item usage
        const response = MockAIService.generateRandomEvent(character, gameData);
        result = `You use ${selectedItem.name} to ${customAction.toLowerCase()}. ${response.content}`;
      } else {
        // Pure custom action
        const response = MockAIService.generateRandomEvent(character, gameData);
        result = `You attempt to ${customAction.toLowerCase()}. ${response.content}`;
      }

      setActionResult(result);
      setIsGenerating(false);
    }, 1500);
  };

  const executeAction = () => {
    if (actionResult) {
      onExecuteAction(customAction, actionResult, selectedItem || undefined);
      reset();
      onClose();
    }
  };

  const reset = () => {
    setCustomAction("");
    setSelectedItem(null);
    setActionResult("");
    setIsGenerating(false);
  };

  return (
    <Dialog open={isOpen} onOpenChange={(open) => { if (!open) { reset(); onClose(); } }}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-yellow-300 flex items-center gap-2">
            <Sparkles className="w-5 h-5" />
            Custom Action
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div className="p-4 bg-yellow-900/20 border border-yellow-500/30 rounded-lg">
            <p className="text-sm text-slate-300">
              Describe what you want your dragon to do. Be creative! You can interact with other dragons, 
              explore locations, use items, or try anything you can imagine within the Wings of Fire universe.
            </p>
          </div>

          {/* Action Input */}
          <div className="space-y-2">
            <Label htmlFor="action">What do you want to do?</Label>
            <Input
              id="action"
              value={customAction}
              onChange={(e) => setCustomAction(e.target.value)}
              placeholder="e.g., 'Approach the group of SkyWing dragonets and ask about the missing scroll' or 'Use my fire breath to light the ancient torch'"
              className="bg-black/50 border-yellow-500/30"
              disabled={isGenerating}
            />
          </div>

          {/* Inventory Selection */}
          {inventory.length > 0 && (
            <div className="space-y-3">
              <Label>Use an item (optional):</Label>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                <Button
                  variant={selectedItem === null ? "default" : "outline"}
                  onClick={() => setSelectedItem(null)}
                  className="text-left p-3"
                  disabled={isGenerating}
                >
                  <div className="flex items-center">
                    <Zap className="w-4 h-4 mr-2" />
                    No item (use natural abilities)
                  </div>
                </Button>

                {inventory.slice(0, 5).map((item, index) => (
                  <Button
                    key={index}
                    variant={selectedItem?.id === item.id ? "default" : "outline"}
                    onClick={() => setSelectedItem(item)}
                    className="text-left p-3"
                    disabled={isGenerating}
                  >
                    <div className="flex items-center">
                      <Package className="w-4 h-4 mr-2" />
                      <div>
                        <div className="font-medium text-sm">{item.name}</div>
                        <div className="text-xs text-slate-400">{item.type.replace('_', ' ')}</div>
                      </div>
                    </div>
                  </Button>
                ))}
              </div>

              {selectedItem && (
                <div className="p-3 bg-purple-900/20 border border-purple-500/30 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <Package className="w-4 h-4" />
                    <span className="font-medium text-purple-300">Selected: {selectedItem.name}</span>
                  </div>
                  <p className="text-sm text-slate-300">{selectedItem.description}</p>
                  {selectedItem.enchantments.length > 0 && (
                    <div className="text-sm text-blue-300 mt-2">
                      ‚ú® Enchantments: {selectedItem.enchantments.join(', ')}
                    </div>
                  )}
                </div>
              )}
            </div>
          )}

          {/* Generate Button */}
          <Button
            onClick={generateActionResult}
            disabled={!customAction.trim() || isGenerating}
            className="w-full bg-yellow-600 hover:bg-yellow-700"
          >
            {isGenerating ? (
              <>
                <Sparkles className="w-4 h-4 mr-2 animate-spin" />
                AI is narrating your action...
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                See What Happens
              </>
            )}
          </Button>

          {/* Result Display */}
          {actionResult && (
            <div className="p-4 bg-gradient-to-r from-yellow-900/30 to-black/30 rounded-lg border border-yellow-500/30">
              <div className="flex items-center mb-2">
                <Sparkles className="w-4 h-4 text-yellow-400 mr-2" />
                <h4 className="font-semibold text-yellow-300">Action Result</h4>
              </div>
              <p className="text-sm text-slate-200 leading-relaxed whitespace-pre-line">{actionResult}</p>

              <div className="flex justify-end mt-4">
                <Button
                  onClick={executeAction}
                  className="bg-yellow-600 hover:bg-yellow-700"
                >
                  Commit to This Action
                </Button>
              </div>
            </div>
          )}

          {/* Examples */}
          <div className="p-3 bg-slate-900/20 border border-slate-600/30 rounded-lg">
            <h5 className="font-semibold text-slate-300 mb-2">Example Actions:</h5>
            <div className="text-xs text-slate-400 space-y-1">
              <div>‚Ä¢ "Challenge the arrogant SkyWing to a flying race"</div>
              <div>‚Ä¢ "Sneak into the forbidden library using my RainWing camouflage"</div>
              <div>‚Ä¢ "Ask the wise SeaWing elder about ancient prophecies"</div>
              <div>‚Ä¢ "Use my enchanted amulet to communicate with distant allies"</div>
              <div>‚Ä¢ "Practice my tribal powers in the training caves"</div>
            </div>
          </div>
        </div>

        <div className="flex justify-end mt-6">
          <Button onClick={() => { reset(); onClose(); }} variant="outline">
            Cancel
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/special-power-moda.content:
import { Character } from "@shared/schema";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { MockAIService } from "@/lib/mock-ai-service";
import { Eye, Brain, Sparkles, Zap, RefreshCw } from "lucide-react";
import { useState, useEffect } from "react";

interface SpecialPowerModalProps {
  character: Character;
  powerType: 'prophecy' | 'mindreading' | 'future' | null;
  isOpen: boolean;
  onClose: () => void;
  onUsePower: (power: string, result: string) => void;
}

export default function SpecialPowerModal({ 
  character, 
  powerType, 
  isOpen, 
  onClose, 
  onUsePower 
}: SpecialPowerModalProps) {
  const [currentVision, setCurrentVision] = useState<string>("");
  const [isGenerating, setIsGenerating] = useState(false);
  const [usageCount, setUsageCount] = useState(0);
  const [soulCost, setSoulCost] = useState(0);

  const powerConfig = {
    prophecy: {
      title: "Prophecy Vision",
      icon: <Eye className="w-5 h-5" />,
      color: "blue",
      description: "Peer into the threads of fate to see what destiny holds",
      baseCost: 2,
      actions: [
        "See a vision of the near future",
        "Divine the outcome of a major decision", 
        "Glimpse potential dangers ahead",
        "Witness distant events unfolding",
        "Perceive the consequences of current actions"
      ]
    },
    mindreading: {
      title: "Mind Reading",
      icon: <Brain className="w-5 h-5" />,
      color: "purple", 
      description: "Delve into the thoughts and memories of others",
      baseCost: 3,
      actions: [
        "Read the surface thoughts of a nearby dragon",
        "Probe deeper into someone's memories",
        "Sense the emotions and intentions around you",
        "Detect lies and hidden motives",
        "Communicate telepathically with another mind"
      ]
    },
    future: {
      title: "Future Sight",
      icon: <Sparkles className="w-5 h-5" />,
      color: "cyan",
      description: "See across time with enhanced foresight",
      baseCost: 5,
      actions: [
        "Glimpse multiple possible futures",
        "See the long-term consequences of actions",
        "Witness events that will shape the world",
        "Perceive threats before they manifest",
        "View the destiny of individuals or kingdoms"
      ]
    }
  };

  const config = powerType ? powerConfig[powerType] : null;

  useEffect(() => {
    if (powerType && isOpen) {
      setUsageCount(0);
      setSoulCost(config?.baseCost || 0);
      setCurrentVision("");
    }
  }, [powerType, isOpen, config]);

  const generateVision = (actionType: string) => {
    if (!config || !powerType) return;

    setIsGenerating(true);

    // Simulate AI generation delay
    setTimeout(() => {
      let vision = "";

      switch (powerType) {
        case 'prophecy':
          const prophecy = MockAIService.generateProphecy(character, { action: actionType });
          vision = prophecy.content;
          break;

        case 'mindreading':
          const mindRead = MockAIService.generateVision(character, { action: actionType });
          vision = mindRead.content;
          break;

        case 'future':
          const futureVision = MockAIService.generateProphecy(character, { action: actionType, type: 'future' });
          vision = futureVision.content;
          break;
      }

      setCurrentVision(vision);
      setUsageCount(prev => prev + 1);
      setSoulCost(prev => prev + (config.baseCost * usageCount));
      setIsGenerating(false);
    }, 1000);
  };

  const usePower = () => {
    if (currentVision && config && powerType) {
      onUsePower(config.title, currentVision);
      onClose();
    }
  };

  if (!config || !powerType) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className={`text-${config.color}-300 flex items-center gap-2`}>
            {config.icon}
            {config.title}
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div className={`p-4 bg-${config.color}-900/20 border border-${config.color}-500/30 rounded-lg`}>
            <p className="text-sm text-slate-300">{config.description}</p>

            {usageCount > 0 && (
              <div className="flex items-center gap-4 mt-2 text-xs">
                <Badge variant="outline">Uses: {usageCount}</Badge>
                <Badge variant="destructive">Soul Cost: {soulCost}%</Badge>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="space-y-3">
            <h4 className="font-semibold text-slate-200">Choose your vision:</h4>
            {config.actions.map((action, index) => (
              <Button
                key={index}
                variant="outline"
                className={`w-full text-left p-3 border-${config.color}-500/30 hover:bg-${config.color}-500/10`}
                onClick={() => generateVision(action)}
                disabled={isGenerating}
              >
                <div className="flex items-center justify-between">
                  <span className="text-sm">{action}</span>
                  {isGenerating ? (
                    <RefreshCw className="w-4 h-4 animate-spin" />
                  ) : (
                    <Zap className="w-4 h-4" />
                  )}
                </div>
              </Button>
            ))}
          </div>

          {/* Vision Display */}
          {currentVision && (
            <div className={`p-4 bg-gradient-to-r from-${config.color}-900/30 to-black/30 rounded-lg border border-${config.color}-500/30`}>
              <div className="flex items-center mb-2">
                {config.icon}
                <h4 className={`font-semibold text-${config.color}-300 ml-2`}>Vision Received</h4>
              </div>
              <p className="text-sm text-slate-200 italic leading-relaxed">{currentVision}</p>

              <div className="flex justify-end mt-4">
                <Button
                  onClick={usePower}
                  className={`bg-${config.color}-600 hover:bg-${config.color}-700`}
                >
                  Use This Vision
                </Button>
              </div>
            </div>
          )}

          {/* Warning */}
          <div className={`p-3 bg-${config.color}-900/10 border border-${config.color}-700/30 rounded-lg`}>
            <p className="text-xs text-slate-400">
              <strong>Warning:</strong> Using {config.title.toLowerCase()} repeatedly may strain your mind and cost soul energy. 
              Each use becomes more taxing than the last.
            </p>
          </div>
        </div>

        <div className="flex justify-end mt-6">
          <Button onClick={onClose} variant="outline">
            Close
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

